import {
  backgroundNoise,
  isMobile,
  loadPoseNet,
  updateStartButton,
  canvas,
  initScene,
  initRenderer,
  initLights,
  loadFruitsModels,
  initSounds,
  render,
  resetCamera,
  initSceneGeometry,
  initTrailRenderers,
  animate,
  camera,
} from "../utils/full-code.js";
(canvas.width = window.innerWidth),
  (canvas.height = window.innerHeight),
  (window.onload = async () => {
    isMobile() && displayMobileScreen(),
      initSounds(),
      await loadPoseNet(),
      initScene(),
      initRenderer(),
      initLights(),
      loadFruitsModels(),
      initSceneGeometry(function () {
        initTrailRenderers(), updateStartButton();
      });
  });
const displayMobileScreen = () => {
  const e = document.getElementsByClassName("intro")[0];
  e.style.display = "none";
  const t = document.getElementsByClassName("mobile-intro")[0];
  t.style.display = "block";
};
window.addEventListener(
  "resize",
  () => {
    camera && resetCamera(), render();
  },
  !1
),
  (document.getElementsByTagName("button")[0].onclick = () => {
    (document.getElementsByClassName("intro")[0].style.display = "none"),
      (document.getElementsByClassName("score")[0].style.display = "block"),
      backgroundNoise.play(),
      animate();
  }),
  console.log(
    "%c%s",
    "color: white; background: purple; font-size: 18px;",
    " Made by Charlie Gerard ðŸŽ‰",
    "https://twitter.com/devdevcharlie"
  );
